        ayahInformations.add(new AyahInformation(10,2,"في قلوبهم مرض فزادهم الله مرضا ولهم عذاب أليم بما كانوا يكذبون" ,1050,730, 375,965));
        ayahInformations.add(new AyahInformation(11,2,"وإذا قيل لهم لا تفسدوا في الأرض قالوا إنما نحن مصلحون" ,370,845, 290,1085));
        ayahInformations.add(new AyahInformation(12,2,"ألا إنهم هم المفسدون ولكن لا يشعرون" ,295,965, 825,1210));
        ayahInformations.add(new AyahInformation(13,2,"وإذا قيل لهم آمنوا كما آمن الناس قالوا أنؤمن كما آمن السفهاء ألا إنهم هم السفهاء ولكن لا يعلمون" ,425,1090, 275,1460));
        ayahInformations.add(new AyahInformation(14,2,"وإذا لقوا الذين آمنوا قالوا آمنا وإذا خلوا إلى شياطينهم قالوا إنا معكم إنما نحن مستهزؤون" ,275,1345, 700,1710));
        ayahInformations.add(new AyahInformation(15,2,"الله يستهزىء بهم ويمدهم في طغيانهم يعمهون" ,700,1590, 840,1840));
        ayahInformations.add(new AyahInformation(16,2,"أولئك الذين اشتروا الضلالة بالهدى فما ربحت تجارتهم وما كانوا مهتدين" ,840,1725, 75,1955));
