        ayahInformations.add(new AyahInformation(23,2,"وإن كنتم في ريب مما نزلنا على عبدنا فأتوا بسورة من مثله وادعوا شهداءكم من دون الله إن كنتم صادقين" ,1600,1480, 1000,1840));
        pageInformation = new PageInformation(4,ayahInformations);
